ADD CONSTRAINT vfs_users_ibfk_1 FOREIGN KEY (ID_POSITION) REFERENCES vfs_positions (ID_POSITION);

DROP VIEW IF EXISTS vfs_users_groups;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW vfs_users_groups AS (
    SELECT SQL_NO_CACHE b.USR, a.GRP, a.NAME
      FROM (vfs_groups a JOIN vfs_groups_users b)
     WHERE a.GRP=b.GRP AND a.BEGDA<=CURRENT_DATE AND a.BEGDA IS NOT NULL AND (a.ENDDA>=CURRENT_DATE OR a.ENDDA IS NULL)
  ORDER BY b.USR, b.GRP
);

DROP VIEW IF EXISTS vfs_users_groups_classes;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW vfs_users_groups_classes AS (
    SELECT SQL_NO_CACHE
           c.GRP, a.PKG, b.PID, b.PKC, b.FTR, c.ACL, a.CODE, a.NAME AS PKG_NAME, b.NAME AS PKC_NAME, b.DISP, b.TYPE, b.TITLE, b.DESC
      FROM ((vfs_packages a JOIN vfs_packages_classes b) JOIN vfs_groups_classes c)
     WHERE a.PKG=b.PKG AND c.PKG=b.PKG AND c.PKC=b.PKC AND a.CHECKED=1 AND b.CHECKED=1
  ORDER BY c.PKG, c.PKC
);
